import React from 'react'
import http, { request } from '../../utils/http';
import '../../utils/flexible'
import './cart.scss'
class Cart extends React.Component {
    constructor() {
        super()
        this.state = {
            // 推荐商品信息
            goods:[]
        }
    }
    // 请求数据
    async componentDidMount(){
        let p=await http.get('/db/tuijian.json');
        if(p.code=="200"){
            console.log("请求成功")
            // 获取推荐商品信息
            this.setState({
                goods:p.data
            })
            console.log(this.state.goods)
        } else{
            console.log("请求失败")
        }
    }
    render() {
        return (
            <div style={{ height: "100%",background: "#fff" }}>
                <section style={{ height: "100%" }}>
                    {/* 购物车部分 */}
                    <div className="com-msgbox">
                        <i className="ico-mall i-cart"></i>
                        <div className="msg">购物车好空呀，快去选购吧~</div>
                        <a href="/lol" className="btn-com nuxt-link-active">去逛逛</a>
                    </div>
                </section>

                {/* 推荐 */}
                <section class="goods-listbox cartRecommend">
                    <section class="listbox">
                        <div class="list-tit"><h3>为你推荐</h3></div>
                        <ul class="goods-list">
                            <li>
                                <a href="/lol/detail/12805" class="list-link">
                                    <div class="list-img">
                                        <img src="https://game.gtimg.cn/images/zb/x5/uploadImg/goods/202001/20200102120303_37392.jpg" lazy="loaded" />
                                    </div>
                                    <div class="list-bd">
                                        <div class="name">
                                            <h5>约德尔人迷你手办套装</h5>
                                        </div>
                                        <div class="price">
                                            <p class="new-pri">¥ 220.00</p>
                                        </div>
                                    </div>
                                </a>
                            </li>
                        </ul>
                    </section>
                </section>
            </div>
        )

    }
}

export default Cart;
