import React from 'react'
import http, { request } from '../../utils/http';
import '../../utils/flexible'
import './cart.scss'
class Cart extends React.Component {
    constructor() {
        super()
        this.state = {
            // 推荐商品信息
            goods: []
        }
    }
    // 请求数据
    async componentDidMount() {
        let p = await http.get('/db/tuijian.json');
        if (p.code == "200") {
            console.log("请求成功")
            // 获取推荐商品信息
            this.setState({
                goods: p.data
            })
            console.log(this.state.goods)
        } else {
            console.log("请求失败")
        }
    }
    render() {
        return (
            <div style={{ height: "100%", background: "#fff" }}>
                <section style={{ height: "100%" }}>
                    {/* 购物车部分 */}
                    {/* 没有商品的时候 */}
                    <div className="com-msgbox">
                        <i className="ico-mall i-cart"></i>
                        <div className="msg">购物车好空呀，快去选购吧~</div>
                        <a href="/lol" className="btn-com nuxt-link-active">去逛逛</a>
                    </div>

                    {/* 有商品的时候 */}
                    <div class="cart-list">
                        <div class="cart-item">
                            <div class="item-hd">
                                <a href="javascript:;" class="btn-check">
                                    <i class="ico-mall i-check"></i>
                                </a>
                                <div class="item-hdimg">
                                    <img src="https://game.gtimg.cn/images/daoju/base/logo/biz/lol.png" lazy="loaded" />
                                </div>
                                <p class="item-hdname">英雄联盟供应商：合泰文化</p>
                            </div>
                        </div>
                        {/* 商品列表 */}
                    
                    </div>
                </section>

                {/* 推荐 */}
                <section className="goods-listbox cartRecommend">
                    <section className="listbox">
                        <div className="list-tit">
                            <h3>为你推荐</h3>
                        </div>
                        <ul className="goods-list">
                            {
                                this.state.goods.map(item => (
                                    <li key={item.iMallId}>
                                        <a href={`/lol/detail/${item.iMallId}`} className="list-link">
                                            <div className="list-img">
                                                <img src={item.sProfileImg} lazy="loaded" />
                                            </div>
                                            <div className="list-bd">
                                                <div className="name">
                                                    <h5>{item.sMallName}</h5>
                                                </div>
                                                <div className="price">
                                                    <p className="new-pri">¥ {item.iPrice}</p>
                                                </div>
                                            </div>
                                        </a>
                                    </li>
                                ))
                            }

                        </ul>
                    </section>
                </section>
            </div>
        )

    }
}

export default Cart;
