import React from 'react'
import http, { request } from '../../utils/http';
import '../../utils/flexible'
import './cart.scss'
class Cart extends React.Component {
    constructor() {
        super()
        this.state = {
            // 推荐商品信息
            goods: []
        }
    }
    // 请求数据
    async componentDidMount() {
        let p = await http.get('/db/tuijian.json');
        if (p.code == "200") {
            console.log("请求成功")
            // 获取推荐商品信息
            this.setState({
                goods: p.data
            })
            console.log(this.state.goods)
        } else {
            console.log("请求失败")
        }
    }
    render() {
        return (
            <div style={{ height: "100%", background: "#fff" }}>
                <section style={{ height: "100%" }}>
                    {/* 购物车部分 */}
                    <div className="com-msgbox">
                        <i className="ico-mall i-cart"></i>
                        <div className="msg">购物车好空呀，快去选购吧~</div>
                        <a href="/lol" className="btn-com nuxt-link-active">去逛逛</a>
                    </div>
                </section>

                {/* 推荐 */}
                <section class="goods-listbox cartRecommend">
                    <section class="listbox">
                        <div class="list-tit">
                            <h3>为你推荐</h3>
                        </div>
                        <ul class="goods-list">
                            {
                                this.state.goods.map(item => (
                                    <>
                                    <li key="item.id">
                                        <a href={"/lol/detail/+{this.state.id}"} class="list-link">
                                            <div class="list-img">
                                                <img src={item.sProfileImg} lazy="loaded" />
                                            </div>
                                            <div class="list-bd">
                                                <div class="name">
                                                    <h5>{item.sMallName}</h5>
                                                </div>
                                                <div class="price">
                                                    <p class="new-pri">¥ {item.iPrice}</p>
                                                </div>
                                            </div>
                                        </a>
                                    </li>
                                    </>
                                ))
                            }

                        </ul>
                    </section>
                </section>
            </div>
        )

    }
}

export default Cart;
